blockquote {
  text-align: center;
  color: #22292f;
  font-weight: 700;
  padding-top: 1rem;
  padding-bottom: 1rem;
  margin-top: 1rem;
  margin-bottom: 1rem;
  font-size: 1.25rem;
  border-top-width: 1px;
  border-bottom-width: 1px;
  border-color: #dae1e7;
}

  @media (min-width: 768px) {blockquote {
    padding-top: 2rem;
    padding-bottom: 2rem;
    margin-top: 2rem;
    margin-bottom: 2rem;
    font-size: 1.875rem;
}
  }

  @media (min-width: 1200px) {blockquote {
    font-size: 2.25rem;
}
  }

blockquote > cite,
blockquote > .cite {
  display: block;
  width: 100%;
  margin-top: .75rem;
}
